// Sécurité : empêcher l'accès direct
if (!defined('ABSPATH')) {
    exit;
}

// APPLICATION DIRECTE DES STYLES META BOX
add_action('admin_head', function() {
    if (!is_admin()) return;

    echo "\n<!-- SNIPPET 203 - META BOX STYLES ACTIF -->\n";
    echo '<style type="text/css" id="snippet-203-metabox-styles">';

    // CSS Meta Box optimisé
    echo '
    /* STYLES META BOX - SNIPPET 203 */
    .postbox .rwmb-meta-box {
        padding: 6px !important;
        margin: 0 !important;
        background: #fff !important;
    }

    .rwmb-field {
        margin-bottom: 6px !important;
        margin-top: 0 !important;
        position: relative;
    }

    .rwmb-label {
        font-weight: 500 !important;
        color: #1e1e1e !important;
        margin-bottom: 3px !important;
        margin-top: 0 !important;
        font-size: 11px !important;
        line-height: 1.3 !important;
        text-transform: uppercase !important;
        letter-spacing: 0.3px !important;
    }

    .rwmb-label + .rwmb-input,
    .rwmb-column .rwmb-label ~ .rwmb-input {
        margin-top: 0 !important;
    }

    .rwmb-input input[type="text"],
    .rwmb-input input[type="email"],
    .rwmb-input input[type="url"],
    .rwmb-input input[type="password"],
    .rwmb-input input[type="number"],
    .rwmb-input input[type="tel"],
    .rwmb-input input[type="date"],
    .rwmb-input input[type="time"],
    .rwmb-input textarea,
    .rwmb-input select {
        padding: 6px 10px !important;
        font-size: 13px !important;
        line-height: 1.4 !important;
        height: 32px !important;
        border: 1px solid #c3c4c7 !important;
        border-radius: 4px !important;
        margin: 0 !important;
        background: #fff !important;
        transition: all 0.15s ease-in-out !important;
        box-shadow: inset 0 1px 2px rgba(0,0,0,0.04) !important;
    }

    .rwmb-input textarea {
        min-height: 70px !important;
        height: auto !important;
        resize: vertical !important;
        padding: 8px 10px !important;
        line-height: 1.5 !important;
    }

    .rwmb-input select {
        height: 32px !important;
        padding: 4px 10px !important;
    }

    .rwmb-input input:focus,
    .rwmb-input textarea:focus,
    .rwmb-input select:focus {
        border-color: #2271b1 !important;
        box-shadow: 0 0 0 1px #2271b1 !important;
        outline: none !important;
    }

    .rwmb-column {
        padding-left: 4px !important;
        padding-right: 4px !important;
    }

    .rwmb-column:first-child {
        padding-left: 0 !important;
    }

    .rwmb-column:last-child {
        padding-right: 0 !important;
    }

    .rwmb-column .rwmb-field {
        margin-bottom: 5px !important;
    }

    .rwmb-input input[type="checkbox"],
    .rwmb-input input[type="radio"] {
        width: 16px !important;
        height: 16px !important;
        margin-right: 6px !important;
        margin-top: 0 !important;
        margin-bottom: 0 !important;
        accent-color: #2271b1 !important;
    }

    .rwmb-choice {
        margin-bottom: 6px !important;
        line-height: 1.4 !important;
        display: flex !important;
        align-items: center !important;
    }

    .rwmb-choice label {
        font-weight: 400 !important;
        margin-bottom: 0 !important;
        font-size: 13px !important;
        color: #1e1e1e !important;
    }

    .rwmb-group-clone {
        padding: 8px !important;
        margin-bottom: 8px !important;
        border: 1px solid #dcdcde !important;
        border-radius: 6px !important;
        background: #f9f9f9 !important;
        position: relative !important;
    }

    .rwmb-clone {
        background: #f6f7f7 !important;
        border: 1px solid #dcdcde !important;
        border-radius: 4px !important;
        padding: 6px !important;
        margin-bottom: 6px !important;
    }

    .rwmb-button,
    .rwmb-input .button {
        padding: 6px 12px !important;
        font-size: 12px !important;
        height: auto !important;
        line-height: 1.4 !important;
        margin-top: 4px !important;
        border-radius: 4px !important;
        font-weight: 500 !important;
        transition: all 0.15s ease !important;
    }

    .rwmb-tab-panel {
        padding: 12px !important;
        background: #fff !important;
        border-radius: 0 4px 4px 4px !important;
    }

    .rwmb-tab-nav {
        border-bottom: 1px solid #dcdcde !important;
        margin-bottom: 0 !important;
    }

    .rwmb-tab-nav a {
        padding: 8px 16px !important;
        font-size: 13px !important;
        font-weight: 500 !important;
        border-radius: 4px 4px 0 0 !important;
        transition: all 0.15s ease !important;
    }

    .rwmb-tab-nav .rwmb-tab-active a {
        background: #fff !important;
        border-bottom: 1px solid #fff !important;
        color: #2271b1 !important;
    }
    ';

    echo '</style>';
    echo "\n<!-- FIN SNIPPET 203 - META BOX STYLES -->\n";
}, 1);

// Test de diagnostic - Notification admin
add_action('admin_notices', function() {
    if (is_admin() && current_user_can('manage_options')) {
        echo '<div class="notice notice-success is-dismissible"><p><strong>Snippet 203 Meta Box:</strong> Styles CSS actifs et chargés!</p></div>';
    }
});

/**
 * Fonction utilitaire pour ajouter des styles personnalisés
 * 
 * @param string $custom_css CSS personnalisé à ajouter
 */
function add_custom_metabox_styles($custom_css) {
    add_action('admin_head', function() use ($custom_css) {
        echo '<style type="text/css">' . $custom_css . '</style>';
    });
}

